import React from "react";
import { withFormik, ErrorMessage, useFormik } from "formik";

import * as Yup from 'yup';


const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao:'',
  emailInstituicao:''
};

const validationSchema = Yup.object().shape({
  nomeInstituicao: Yup.string()
    .label('Nome instituição visitante')
    .required('Valor não pde ser vazio'),
})


const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};


const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validationSchema: validationSchema,
    onSubmit
  });


  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <input placeholder="Instituição visitante"
          id="nomeInstituicao"
          name="nomeInstituicao"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.nomeInstituicao}
        />
        {formik.touched.nomeInstituicao && formik.errors.nomeInstituicao ? (
          <div className="input-feedback" >{formik.errors.nomeInstituicao}</div>
        ) : null}

