import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';


const initialValues = {
    nomeInstituicao: "",
    telefoneInstituicao: "",
    emailInstituicao: "",
    nomeResponsavel: "",
    telefoneResponsavel: "",
    emailResponsavel: "",
    nVisitantes: "",
    dataVisita: "",
    objetivoCaract: "",
    horario: "",
    opcoes: ""

  };

  const validationSchema = Yup.object().shape({
    nomeInstituicao: Yup.string()
      .label('Nome')
      .email('Enter a valid Name')
      .required('Please enter a registered name'),
    emailInstituicao: Yup.string()
      .label('Email')
      .email('Enter a valid email')
      .required('Please enter a registered email'),

  })

const SignupForm = () => {
  const formik = useFormik({
    initialValues,
    validationSchema,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <label htmlFor="firstName">First Name</label>
      <input name="firstName" {...formik.getFieldProps('firstName')} />
      {formik.touched.firstName && formik.errors.firstName ? (
        <div>{formik.errors.firstName}</div>
      ) : null}
      <label htmlFor="lastName">Last Name</label>
      <input name="lastName" {...formik.getFieldProps('lastName')} />
      {formik.touched.lastName && formik.errors.lastName ? (
        <div>{formik.errors.lastName}</div>
      ) : null}
      <label htmlFor="email">Email Address</label>
      <input name="email" {...formik.getFieldProps('email')} />
      {formik.touched.email && formik.errors.email ? (
        <div>{formik.errors.email}</div>
      ) : null}
      <button type="submit">Submit</button>
    </form>
  );
};

export default SignupForm
