import React from "react";
import { useFormik } from "formik";

import * as Yup from 'yup';


const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailresponsavel: "",
};

const validationSchema = Yup.object().shape({
  nomeInstituicao: Yup.string()
    .label('Nome instituição visitante')
    .required('Valor não pode ser vazio'),
  telefoneInstituicao: Yup.string()
    .label('Telefone da instituição visitante')
    .required('Valor não pode ser vazio'),
  emailInstituicao: Yup.string()
    .label('Nome instituição visitante')
    .email("Adicione um e-amil válido")
    .required('Valor não pode ser vazio'),
    nomeResponsavel: Yup.string()
    .label('Nome instituição visitante')
    .required('Valor não pode ser vazio'),
    telefoneResponsavel: Yup.string()
    .label('Telefone da instituição visitante')
    .required('Valor não pode ser vazio'),
    emailresponsavel: Yup.string()
    .label('Nome instituição visitante')
    .email("Adicione um e-amil válido")
    .required('Valor não pode ser vazio'),
})


const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};


const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validationSchema: validationSchema,
    onSubmit
  });


  return (
    <form onSubmit={formik.handleSubmit}>

      <div>
        <input placeholder="Instituição visitante"
          id="nomeInstituicao"
          name="nomeInstituicao"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.nomeInstituicao && formik.errors.nomeInstituicao ? (
          <div className="input-feedback" >{formik.errors.nomeInstituicao}</div>
        ) : null}

        <input placeholder="Telefone da Instituição visitante"
          id="telefoneInstituicao"
          name="telefoneInstituicao"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.telefoneInstituicao && formik.errors.telefoneInstituicao ? (
          <div className="input-feedback" >{formik.errors.telefoneInstituicao}</div>
        ) : null}


        <input placeholder="Instituição visitante"
          id="emailInstituicao"
          name="emailInstituicao"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.emailInstituicao && formik.errors.emailInstituicao ? (
          <div className="input-feedback" >{formik.errors.emailInstituicao}</div>
        ) : null}
      </div>

      <div>
        <input placeholder="Responsável pela visitante"
          id="nomeResponsavel"
          name="nomeResponsavel"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.nomeResponsavel && formik.errors.nomeResponsavel ? (
          <div className="input-feedback" >{formik.errors.nomeResponsavel}</div>
        ) : null}

        <input placeholder="Telefone do Responsável pela visita"
          id="telefoneResponsavel"
          name="telefoneResponsavel"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.telefoneResponsavel && formik.errors.telefoneResponsavel ? (
          <div className="input-feedback" >{formik.errors.telefoneResponsavel}</div>
        ) : null}


        <input placeholder="Instituição visitante"
          id="emailInstituicao"
          name="emailInstituicao"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.emailInstituicao && formik.errors.emailInstituicao ? (
          <div className="input-feedback" >{formik.errors.emailInstituicao}</div>
        ) : null}
      </div>

      <button type="submit">Enviar</button>


    </form>
  );
};

export default INSAForm
