import React from "react";
import { useFormik } from "formik";


const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailResponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaract: "",
  horario: "",
  opcoes: ""

};

const validate = values => {
  const errors = {};
  if (!values.nomeInstituicao) {
    errors.nomeInstituicao = 'Instituição Visitante não pode ser vazio';
  }
  return errors;

};



const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};






const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validate,
    onSubmit



  });


  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <input 
                  id="nomeInstituicao"
                  name="nomeInstituicao"

                  type="text"

        placeholder="Instituição visitante"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.nomeInstituicao} />

{formik.touched.nomeInstituicao && formik.errors.nomeInstituicao ? (
          <div  className="input-feedback" >{formik.errors.nomeInstituicao}</div>
        ) : null}

      </div>
      <button type="submit">Enviar</button>

    </form>
  );
};

export default INSAForm
