import React from 'react';
import MaskedInput from 'react-text-mask';
import classnames from 'classnames';
import Label from './Label';
import InputFeedback from './InputFeedback';


const TelephoneInput = ({ type, id, label, placeholder, error, onChange, className, mask, value, onBlur }) => {
    const classes = classnames(
        'input-element',
        {
            'error': !!error,
        },
        className
    );

    function beforeMaskedStateChange({ value }) {
        if (value!='undefined' && value.endsWith("/")) {
          value = value.slice(0, -1);
        }

        return {
          value
        };
      }
    return (
        <div className={classes}>
            <Label htmlFor={id} error={error}>
                {label}
            </Label>
            <MaskedInput
                id={id}
                className="text-input"
                type={type}
                placeholder={placeholder}
                onChange={onChange}
                mask={mask}
                value = {beforeMaskedStateChange}
                onBlur={onBlur}

                ngModelChange="contact.phone = unmask($event)"  />

            />
            <InputFeedback error={error} />


        </div>
    );
};

export default TelephoneInput;
