import React from "react";
import { withFormik, ErrorMessage, useFormik } from "formik";
import * as Yup from 'yup';



const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailResponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaract: "",
  horario: "",
  opcoes: ""

};

const validate = values => {
  const errors = {};
  if (!values.nomeInstituicao) {
    errors.nomeInstituicao = 'Instituição Visitante não pode ser vazio';
  }
  return errors;

};



const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};






const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validate,
    onSubmit



  });


  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <input placeholder="Instituição visitante"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.nomeInstituicao} />
<ErrorMessage name="nomeInstituicao">{msg => <div>{msg}</div>}</ErrorMessage>


      </div>
      <button type="submit">Enviar</button>

    </form>
  );
};

export default INSAForm
