import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';


const initialValues = {
    nomeInstituicao: "",
    nomeResponsavel: "",


  };

const SignupForm = () => {
  const formik = useFormik({
    initialValues,
    validationSchema: Yup.object({
        nomeInstituicao: Yup.string()
        .max(15, 'Must be 15 characters or less')
        .required('Required'),
        emailInstituicao: Yup.string()
        .email('Invalid email address')
        .required('Required'),
    }),
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <label htmlFor="nomeInstituicao">First Name</label>
      <input name="nomeInstituicao" {...formik.getFieldProps('nomeInstituicao')} />
      {formik.touched.nomeInstituicao && formik.errors.nomeInstituicao ? (
        <div>{formik.errors.nomeInstituicao}</div>
      ) : null}
      <label htmlFor="emailInstituicao">Email Address</label>
      <input name="emailInstituicao" {...formik.getFieldProps('emailInstituicao')} />
      {formik.touched.emailInstituicao && formik.errors.emailInstituicao ? (
        <div>{formik.errors.emailInstituicao}</div>
      ) : null}
      <button type="submit">Submit</button>
    </form>
  );
};

export default SignupForm
