import React from "react";
import { useFormik } from "formik";

import * as Yup from 'yup';
import classnames from 'classnames';



const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailresponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaracteristicas: "",
  horario: ""
};

const validationSchema = Yup.object().shape({
  nomeInstituicao: Yup.string()
    .label('Nome instituição visitante')
    .required('Valor não pode ser vazio'),
  telefoneInstituicao: Yup.string()
    .label('Telefone da instituição visitante')
    .required('Valor não pode ser vazio'),
  emailInstituicao: Yup.string()
    .label('Nome instituição visitante')
    .email("Adicione um e-amil válido")
    .required('Valor não pode ser vazio'),
  nomeResponsavel: Yup.string()
    .label('Nome instituição visitante')
    .required('Valor não pode ser vazio'),
  telefoneResponsavel: Yup.string()
    .label('Telefone da instituição visitante')
    .required('Valor não pode ser vazio'),
  emailresponsavel: Yup.string()
    .label('Nome instituição visitante')
    .email("Adicione um e-amil válido")
    .required('Valor não pode ser vazio'),
  nVisitantes: Yup.number()
    .label('Número de visitantes')
    .required('Valor não pode ser vazio'),
  dataVisita: Yup.date()
    .label('Data da visita')
    .required('Valor não pode ser vazio'),
  objetivoCaracteristicas: Yup.string()
    .label('Objetivo e características da turma visitante')
    .required('Valor não pode ser vazio'),
  horario: Yup.string()
    .label('Horário da visita')
    .required('Escolha um horário para visita')
})


const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};

const InputFeedback = ({ error }) =>
  error ? <div className="input-feedback">{error}</div> : null;

const Label = ({ error, className, children, ...props }) => {
  return (
    <label className="label" {...props}>
      {children}
    </label>
  );
};

const TextInput = ({ type, id, label, error, onChange, className }) => {
  const classes = classnames(
    'input-group',
    className
  );
  return (
    <div className={classes}>
      <Label htmlFor={id} error={error}>
        {label}
      </Label>
      <input
        id={id}
        className="text-input"
        type={type}
        onChange={onChange}
      />
      <InputFeedback error={error} />
    </div>
  );
};




const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validationSchema: validationSchema,
    onSubmit
  });


  return (
    <form onSubmit={formik.handleSubmit}>

      <div>
        <TextInput
          id="nomeInstituicao"
          type="text"
          label="Instituição visitante:"
          placeholder="Instituição visitante"
          error={formik.touched.nomeInstituicao && formik.errors.nomeInstituicao}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />

        <TextInput
          id="telefoneInstituicao"
          type="text"
          label="Telefone da Instituição visitante:"
          placeholder="Telefone da Instituição visitante"
          error={formik.touched.telefoneInstituicao && formik.errors.telefoneInstituicao}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />

        <TextInput
          id="emailInstituicao"
          type="email"
          label="E-mail da Instituição visitante:"
          placeholder="E-mail da Instituição visitante"
          error={formik.touched.emailInstituicao && formik.errors.emailInstituicao}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
      </div>

      <div>

      <TextInput
          id="nomeResponsavel"
          type="text"
          label="Responsável pela visitante:"
          placeholder="Responsável pela visitante"
          error={formik.touched.nomeResponsavel && formik.errors.nomeResponsavel}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />

<TextInput
          id="telefoneResponsavel"
          type="text"
          label="Telefone do Responsável pela visita:"
          placeholder="Telefone do Responsável pela visita"
          error={formik.touched.telefoneResponsavel && formik.errors.telefoneResponsavel}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />


<TextInput
          id="emailresponsavel"
          type="email"
          label="Email do responsável:"
          placeholder="Email do responsável"
          error={formik.touched.emailresponsavel && formik.errors.emailresponsavel}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
      </div>



      <div>
        <TextInput
          id="nVisitantes"
          type="number"
          label="Número de visitantes:"
          placeholder="Número de visitantes"
          valu
          error={formik.touched.nVisitantes && formik.errors.nVisitantes}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />

        <label htmlFor="dataVisita">Data da visita:</label>
        <input
          id="dataVisita"
          name="dataVisita"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.dataVisita && formik.errors.dataVisita ? (
          <div className="input-feedback" >{formik.errors.dataVisita}</div>
        ) : null}

        <label htmlFor="dataVisita">Objetivo e características da turma:</label>
        <input placeholder="Objetivo e características da turma"
          id="objetivoCaracteristicas"
          name="objetivoCaracteristicas"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
        {formik.touched.objetivoCaracteristicas && formik.errors.objetivoCaracteristicas ? (
          <div className="input-feedback" >{formik.errors.objetivoCaracteristicas}</div>
        ) : null}
      </div>

      <label htmlFor="dataVisita">Objetivo e características da turma:</label>
      <input placeholder="Objetivo e características da turma"
        id="objetivoCaracteristicas"
        name="objetivoCaracteristicas"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
      />
      {formik.touched.objetivoCaracteristicas && formik.errors.objetivoCaracteristicas ? (
        <div className="input-feedback" >{formik.errors.objetivoCaracteristicas}</div>
      ) : null}


      <div>
        <label htmlFor="horarios">Horário da visita:</label>
        <div id="horarios">
          <label htmlFor="horarioManha">Manhã: das 8:30h às 11:30h</label>
          <input
            placeholder=""
            id="horarioManha"
            type="radio"
            name="horario"
            value="manha"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />

          <label htmlFor="horarioTarde">Tarde: das 13:30h às 16:30h</label>
          <input
            placeholder=""
            id="horarioTarde"
            type="radio"
            name="horario"
            value="manha"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />

          <label htmlFor="horarioManhaTarde">Manhã e Tarde: das 8:30h às 16:30h</label>
          <input
            placeholder=""
            id="horarioManhaTarde"
            type="radio"
            name="horario"
            value="manha"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />
        </div>

        {formik.touched.horario && formik.errors.horario ? (
          <div className="input-feedback" >{formik.errors.horario}</div>
        ) : null}
      </div>

      <button type="submit">Enviar</button>
      /*


</form>
);
};

export default INSAForm
