import React from "react";
import { withFormik, ErrorMessage } from "formik";
import * as yup from 'yup';



const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailResponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaract: "",
  horario: "",
  opcoes: ""

};

const validationSchema = Yup.object().shape({
  nomeInstituicao: Yup.string()
    .label('Email')
    .email('Enter a valid email')
    .required('Please enter a registered email'),
  emailInstituicao: Yup.string()
    .label('Email')
    .email('Enter a valid email')
    .required('Please enter a registered email'),

})



const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};


const enhanceWithFormik = withFormik({
  initialValues,
  onSubmit,
  validateOnChange: true,

  validationSchema: validationSchema

});




const INSAForm = () => {

  return (
    <form onSubmit={enhanceWithFormik.handleSubmit}>
      <div>
        <input placeholder="Instituição visitante" />
        <ErrorMessage name="nomeInstituicao" />
        <input placeholder="Telefone da Isntituição" />
        <input placeholder="E-mail da intituição" />
      </div>

      <div>
        <input placeholder="Nome do Responsável" />
        <input placeholder="Telefone do responsável" />
        <input placeholder="E-mail do responsável" />
      </div>
      <button type="submit">Enviar</button>

    </form>
  );
};

export default enhanceWithFormik(INSAForm)
