import React from "react";
import { useFormik, ErrorMessage } from "formik";


const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailResponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaract: "",
  horario: "",
  opcoes: ""

};

const validate = values => {
  const errors = {};
  console.log(values);
  if (!values.nomeInstituicao){
    errors.nomeInstituicao = 'Instituição Visitante não pode ser vazio';
  }

  return errors;
};

const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

 };



const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    isInitialValid: false,
    validateOnChange: true,
    validateOnBlur: true,
    displayName: 'MyForm',
    validate,
    onSubmit
  })


 return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <input placeholder="Instituição visitante" />
        <ErrorMessage name="nomeInstituicao" />
        <input placeholder="Telefone da Isntituição" />
        <input placeholder="E-mail da intituição" />
      </div>

      <div>
        <input placeholder="Nome do Responsável" />
        <input placeholder="Telefone do responsável" />
        <input placeholder="E-mail do responsável" />
      </div>
      <button type="submit">Enviar</button>

    </form>
  );
};

export default useFormik(INSAForm)
