import React from "react";
import { useFormik } from "formik";
import * as Yup from 'yup';



const initialValues = {
  nomeInstituicao: "",
  telefoneInstituicao: "",
  emailInstituicao: "",
  nomeResponsavel: "",
  telefoneResponsavel: "",
  emailResponsavel: "",
  nVisitantes: "",
  dataVisita: "",
  objetivoCaract: "",
  horario: "",
  opcoes: ""

};

const validationSchema = Yup.object().shape({
  nomeInstituicao: Yup.string()
    .label('Nome')
    .email('Enter a valid Name')
    .required('Please enter a registered name'),
  emailInstituicao: Yup.string()
    .label('Email')
    .email('Enter a valid email')
    .required('Please enter a registered email'),

})



const onSubmit = values => {
  alert(JSON.stringify(values, null, 2));

};






const INSAForm = () => {

  const formik = useFormik({
    initialValues,
    validationSchema: validationSchema,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },



  });


  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <input placeholder="Instituição visitante"
         id="nomeInstituicao"
         name="nomeInstituicao"
         type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.nomeInstituicao} />
{formik.touched.nomeInstituicao && formik.errors.nomeInstituicao ? (
          <div  className="input-feedback" >{formik.errors.nomeInstituicao}</div>
        ) : null}

      </div>
      <button type="submit">Enviar</button>

    </form>
  );
};

export default INSAForm
