import React from 'react';
import { useFormik } from 'formik';

import { render } from "react-dom";
import { Formik } from "formik";

import "./index.css";


const validate = values => {
  const errors = {};
  if (!values.intituicaoVisitante) {
    errors.intituicaoVisitante = 'Instituição Visitante não pode ser vazio';
  }

  if (!values.lastName) {
    errors.lastName = 'Required';
  } else if (values.lastName.length > 20) {
    errors.lastName = 'Must be 20 characters or less';
  }

  if (!values.emailInstituicao) {
    errors.emailInstituicao = 'Required';
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.emailInstituicao)) {
    errors.emailInstituicao = 'Invalid email address';
  }

  return errors;
};

const INSAForm = () => {
  // Notice that we have to initialize ALL of fields with values. These
  // could come from props, but since we don't want to prefill this form,
  // we just use an empty string. If you don't do this, React will yell
  // at you.
  const formik = useFormik({
    initialValues: {
      intituicaoVisitante: '',
      emailInstituicao: '',
      lastName: '',
    },
    validate,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <label htmlFor="intituicaoVisitante">Nome do responsável</label>
        <input
          id="intituicaoVisitante"
          name="intituicaoVisitante"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.intituicaoVisitante}
        />
        {formik.touched.intituicaoVisitante && formik.errors.intituicaoVisitante ? (
          <div>{formik.errors.intituicaoVisitante}</div>
        ) : null}

        <div className="flex-column">
          <div className="element-form">

          <label htmlFor="intituicaoVisitante">Telefone da instituição visitante</label>
          <input
            id="intituicaoVisitante"
            name="intituicaoVisitante"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.intituicaoVisitante}
            className={
              errors.intituicaoVisitante && touched.intituicaoVisitante
                ? "text-input error"
                : "text-input"
            }
          />
          {formik.touched.intituicaoVisitante && formik.errors.intituicaoVisitante ? (
            <div>{formik.errors.intituicaoVisitante}</div>
          ) : null}
          </div>
          <div className="element-form">
          <label htmlFor="emailInstituicao">E-mail da instituição visitante</label>
          <input
            id="emailInstituicao"
            name="emailInstituicao"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.emailInstituicao}
          />
          {formik.touched.emailInstituicao && formik.errors.emailInstituicao ? (
            <div>{formik.errors.emailInstituicao}</div>
          ) : null}

          </div>

        </div>
      </div>


      <button type="submit">Submit</button>
    </form>
  );
};


render(<INSAForm />, document.getElementById("root"));

